INSERT INTO `introduces` (`id`, `name`, `content`, `slug`, `created_at`, `updated_at`) VALUES
(1, 'GIỚI THIỆU TRƯỜNG ĐỨC', '<p><strong>Trường Đức</strong><strong>&nbsp;</strong>l&agrave; một trang b&aacute;n h&agrave;ng thiết bị c&ocirc;ng nghệ điện tử phục vụ nh&agrave; th&ocirc;ng minh được th&agrave;nh lập bởi 2 con người c&oacute; chung đam m&ecirc;. Xuất ph&aacute;t điểm từ một c&ocirc;ng ty tại Việt Nam chuy&ecirc;n về s&aacute;ng tạo, nghi&ecirc;n cứu, thiết kế v&agrave; sản xuất c&aacute;c thiết bị điều khiển từ xa sử dụng c&aacute;c c&ocirc;ng nghệ s&oacute;ng kh&ocirc;ng d&acirc;y hiện đại, lĩnh vực hoạt động của ch&uacute;ng t&ocirc;i c&oacute; thể ứng dụng rộng r&atilde;i trong ng&agrave;nh d&acirc;n dụng, thiết bị điện th&ocirc;ng minh, smarthome v.v</p>\r\n\r\n<p>Được th&agrave;nh lập từ năm 2017, với kinh nghiệm phong ph&uacute; của ch&uacute;ng t&ocirc;i trong lĩnh vực thiết bị điều khiển v&agrave; nh&agrave; th&ocirc;ng minh, ch&uacute;ng t&ocirc;i nhận thấy tại thị trường Việt Nam tuy c&oacute; nhiều giải ph&aacute;p nh&agrave; th&ocirc;ng minh nhưng phần lớn chỉ tập trung cho đối tượng kh&aacute;ch h&agrave;ng cao cấp, c&oacute; thu nhập cao, hầu hết l&agrave; những giải ph&aacute;p nhập ngoại, chi ph&iacute; triển khai hệ thống lớn, chi ph&iacute; bảo tr&igrave; v&igrave; thế cũng lớn, v&agrave; quan trọng l&agrave; triển khai phức tạp, thời gian l&acirc;u, cần đến nhiều c&ocirc;ng sức từ những chuy&ecirc;n gia chuy&ecirc;n ng&agrave;nh, n&ecirc;n chỉ c&oacute; một số lượng nhỏ c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; điều kiện mới c&oacute; thể tiếp cận được nh&agrave; th&ocirc;ng minh.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i cho rằng đ&atilde; đến l&uacute;c đơn giản h&oacute;a mọi thứ để cuộc sống được th&ocirc;ng minh, dễ chịu hơn cho tất cả mọi người, kể cả những kh&aacute;ch h&agrave;ng chưa c&oacute; điều kiện kinh tế cao bằng c&aacute;ch đem đến người d&ugrave;ng những sản phẩm thiết bị điện th&ocirc;ng minh, s&aacute;ng tạo, cực kỳ dễ lắp đặt v&agrave; sử dụng v&agrave; quan trọng l&agrave; mức gi&aacute; hợp l&yacute;, hợp với t&uacute;i tiền của người Việt Nam.</p>\r\n\r\n<p>Với kinh nghiệm về thương mại điện tử, b&aacute;n h&agrave;ng online&nbsp;ch&uacute;ng t&ocirc;i đ&atilde; thiết lập&nbsp;<strong>Trường Đức</strong>&nbsp;như một k&ecirc;nh ph&acirc;n phối b&aacute;n lẻ online chuy&ecirc;n về thiết bị điện th&ocirc;ng minh, nhằm cung cấp tới mọi kh&aacute;ch h&agrave;ng tr&ecirc;n to&agrave;n quốc dịch vụ b&aacute;n h&agrave;ng tốt nhất, mức gi&aacute; cạnh tranh nhất c&oacute; thể, với mục ti&ecirc;u phổ cập sản phẩm thiết bị nh&agrave; th&ocirc;ng minh tới nhiều đối tượng kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i quyết t&acirc;m trở th&agrave;nh thương hiệu b&aacute;n lẻ thiết bị điện th&ocirc;ng minh số 1 Việt Nam.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i xin cảm ơn những c&ocirc;ng ty đối t&aacute;c, nh&agrave; ph&acirc;n phối, nh&agrave; m&aacute;y sản xuất, v.v. uy t&iacute;n đ&atilde; tin tưởng ch&uacute;ng t&ocirc;i trong dự &aacute;n&nbsp;<strong>Trường Đức&nbsp;</strong>hợp t&aacute;c hiệu quả c&ugrave;ng ch&uacute;ng t&ocirc;i để c&oacute; thể mang sản phẩm tới tay người ti&ecirc;u d&ugrave;ng. Cảm ơn những kh&aacute;ch h&agrave;ng tuyệt vời đ&atilde; khai ph&aacute; cho ch&uacute;ng t&ocirc;i nhiều nhu cầu mới, đ&oacute;ng g&oacute;p &yacute; kiến v&agrave; trao cho ch&uacute;ng t&ocirc;i cơ hội được phục vụ v&agrave; học hỏi như những người bạn ch&acirc;n th&agrave;nh.</p>', 'Giới thiệu', '2023-11-21 23:57:06', '2023-11-21 23:57:06'),
(2, 'ĐẠI LÝ CỦA TRƯỜNG ĐỨC', '<h3><strong>Tại Bu&ocirc;n Ma Thu&ocirc;t:</strong></h3>\r\n\r\n<p><strong>1. C&ocirc;ng ty cổ phần kĩ thuật điện v&agrave; viển th&ocirc;ng Trường Đức</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Địa chỉ: 88 Y Moan &Ecirc;nuoi, phường T&acirc;n lợi&nbsp;, Buon Me Thuot, Vietnam</p>\r\n	</li>\r\n	<li>\r\n	<p>Điện thoại: &nbsp;Mr Đức&nbsp;0989 168 262</p>\r\n	</li>\r\n	<li>\r\n	<p>Website:&nbsp;<a href=\"https://khathy.vn/\">http://tanphong.vn/</a></p>\r\n	</li>\r\n</ul>', 'Đại lý', '2023-11-21 23:59:20', '2023-11-21 23:59:20');

