INSERT INTO `policies` (`id`, `name`, `content`, `slug`, `created_at`, `updated_at`) VALUES
(1, 'GIỚI THIỆU TRƯỜNG ĐỨC', '<p><strong>Trường Đức.vn</strong><strong>&nbsp;</strong>l&agrave; một trang b&aacute;n h&agrave;ng thiết bị c&ocirc;ng nghệ điện tử phục vụ nh&agrave; th&ocirc;ng minh được th&agrave;nh lập bởi 2 con người c&oacute; chung đam m&ecirc;. Xuất ph&aacute;t điểm từ một c&ocirc;ng ty tại Việt Nam chuy&ecirc;n về s&aacute;ng tạo, nghi&ecirc;n cứu, thiết kế v&agrave; sản xuất c&aacute;c thiết bị điều khiển từ xa sử dụng c&aacute;c c&ocirc;ng nghệ s&oacute;ng kh&ocirc;ng d&acirc;y hiện đại, lĩnh vực hoạt động của ch&uacute;ng t&ocirc;i c&oacute; thể ứng dụng rộng r&atilde;i trong ng&agrave;nh d&acirc;n dụng, thiết bị điện th&ocirc;ng minh, smarthome v.v</p>\r\n\r\n<p>Được th&agrave;nh lập từ năm 2017, với kinh nghiệm phong ph&uacute; của ch&uacute;ng t&ocirc;i trong lĩnh vực thiết bị điều khiển v&agrave; nh&agrave; th&ocirc;ng minh, ch&uacute;ng t&ocirc;i nhận thấy tại thị trường Việt Nam tuy c&oacute; nhiều giải ph&aacute;p nh&agrave; th&ocirc;ng minh nhưng phần lớn chỉ tập trung cho đối tượng kh&aacute;ch h&agrave;ng cao cấp, c&oacute; thu nhập cao, hầu hết l&agrave; những giải ph&aacute;p nhập ngoại, chi ph&iacute; triển khai hệ thống lớn, chi ph&iacute; bảo tr&igrave; v&igrave; thế cũng lớn, v&agrave; quan trọng l&agrave; triển khai phức tạp, thời gian l&acirc;u, cần đến nhiều c&ocirc;ng sức từ những chuy&ecirc;n gia chuy&ecirc;n ng&agrave;nh, n&ecirc;n chỉ c&oacute; một số lượng nhỏ c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; điều kiện mới c&oacute; thể tiếp cận được nh&agrave; th&ocirc;ng minh.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i cho rằng đ&atilde; đến l&uacute;c đơn giản h&oacute;a mọi thứ để cuộc sống được th&ocirc;ng minh, dễ chịu hơn cho tất cả mọi người, kể cả những kh&aacute;ch h&agrave;ng chưa c&oacute; điều kiện kinh tế cao bằng c&aacute;ch đem đến người d&ugrave;ng những sản phẩm thiết bị điện th&ocirc;ng minh, s&aacute;ng tạo, cực kỳ dễ lắp đặt v&agrave; sử dụng v&agrave; quan trọng l&agrave; mức gi&aacute; hợp l&yacute;, hợp với t&uacute;i tiền của người Việt Nam.</p>\r\n\r\n<p>Với kinh nghiệm về thương mại điện tử, b&aacute;n h&agrave;ng online&nbsp;ch&uacute;ng t&ocirc;i đ&atilde; thiết lập&nbsp;<strong>Tấn Phong.vn</strong>&nbsp;như một k&ecirc;nh ph&acirc;n phối b&aacute;n lẻ online chuy&ecirc;n về thiết bị điện th&ocirc;ng minh, nhằm cung cấp tới mọi kh&aacute;ch h&agrave;ng tr&ecirc;n to&agrave;n quốc dịch vụ b&aacute;n h&agrave;ng tốt nhất, mức gi&aacute; cạnh tranh nhất c&oacute; thể, với mục ti&ecirc;u phổ cập sản phẩm thiết bị nh&agrave; th&ocirc;ng minh tới nhiều đối tượng kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i quyết t&acirc;m trở th&agrave;nh thương hiệu b&aacute;n lẻ thiết bị điện th&ocirc;ng minh số 1 Việt Nam.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i xin cảm ơn những c&ocirc;ng ty đối t&aacute;c, nh&agrave; ph&acirc;n phối, nh&agrave; m&aacute;y sản xuất, v.v. uy t&iacute;n đ&atilde; tin tưởng ch&uacute;ng t&ocirc;i trong dự &aacute;n&nbsp;<strong>Tấn Phong.vn&nbsp;</strong>hợp t&aacute;c hiệu quả c&ugrave;ng ch&uacute;ng t&ocirc;i để c&oacute; thể mang sản phẩm tới tay người ti&ecirc;u d&ugrave;ng. Cảm ơn những kh&aacute;ch h&agrave;ng tuyệt vời đ&atilde; khai ph&aacute; cho ch&uacute;ng t&ocirc;i nhiều nhu cầu mới, đ&oacute;ng g&oacute;p &yacute; kiến v&agrave; trao cho ch&uacute;ng t&ocirc;i cơ hội được phục vụ v&agrave; học hỏi như những người bạn ch&acirc;n th&agrave;nh.</p>', 'Chính sách', '2023-11-20 23:41:29', '2023-11-20 23:41:29'),
(2, 'CHÍNH SÁCH VẬN CHUYỂN HÀNG HÓA TRƯỜNG ĐỨC', '<p>Để h&agrave;ng h&oacute;a tới tận tay qu&yacute; kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng, an to&agrave;n,&nbsp;<strong>Trường Đức</strong>&nbsp;đ&atilde; hợp t&aacute;c v&agrave; k&yacute; hợp đồng vận chuyển với c&aacute;c đối t&aacute;c l&agrave; c&aacute;c c&ocirc;ng ty chuyển ph&aacute;t h&agrave;ng h&oacute;a chuy&ecirc;n nghiệp như c&ocirc;ng ty Cổ phần bưu ch&iacute;nh Viettel (ViettelPost), Giao h&agrave;ng nhanh, Giao h&agrave;ng tiết kiệm&hellip; Ch&uacute;ng t&ocirc;i &aacute;p dụng ch&iacute;nh s&aacute;ch vận chuyển với to&agrave;n bộ sản phẩm tr&ecirc;n website&nbsp;<strong>Trường Đức</strong>&nbsp;như sau:</p>\r\n\r\n<h3><strong>H&igrave;nh thức chuyển h&agrave;ng:</strong></h3>\r\n\r\n<p>Chuyển ph&aacute;t nhanh &ndash; Cam kết thời gian giao h&agrave;ng từ 1 - 5 ng&agrave;y l&agrave;m việc.</p>\r\n\r\n<p>Nh&acirc;n vi&ecirc;n vận chuyển sẽ li&ecirc;n hệ với số điện thoại di động của qu&yacute; kh&aacute;ch để chuyển h&agrave;ng. Qu&yacute; kh&aacute;ch h&agrave;ng vui l&ograve;ng giữ m&aacute;y bật v&agrave; trong v&ugrave;ng phủ s&oacute;ng trong thời gian sau đặt h&agrave;ng. Trong trường hợp nh&acirc;n vi&ecirc;n vận chuyển kh&ocirc;ng thể gọi điện cho qu&yacute; kh&aacute;ch hoặc phụ thuộc c&aacute;c yếu tố bất khả kh&aacute;ng (thời tiết, giao th&ocirc;ng khắc nghiệt, địa điểm kh&oacute; t&igrave;m v.v) ch&uacute;ng t&ocirc;i kh&ocirc;ng thể đảm bảo thời gian đ&atilde; cam kết.</p>\r\n\r\n<h3><strong>Thời gian nhận h&agrave;ng:</strong></h3>\r\n\r\n<p>Ph&aacute;t h&agrave;ng, nhận h&agrave;ng trong giờ h&agrave;nh ch&iacute;nh, hoạt động kể cả ng&agrave;y thứ 7 trong tuần.</p>\r\n\r\n<h3><strong>V&ugrave;ng chuyển h&agrave;ng:</strong></h3>\r\n\r\n<p>To&agrave;n quốc trong l&atilde;nh thổ Việt Nam.</p>\r\n\r\n<ol start=\"4\">\r\n	<li>\r\n	<h3>Mức ph&iacute; chuyển ph&aacute;t:</h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Miễn ph&iacute; nếu tổng đơn h&agrave;ng &gt;= 500.000đ.</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Với c&aacute;c đơn h&agrave;ng c&oacute; tổng gi&aacute; trị &lt;500.000đ, Tấn Phong.vn</strong>&nbsp;<strong>&nbsp;&aacute;p dụng bảng t&iacute;nh cước vận chuyển theo khu vực vận chuyển.</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Để tiện lợi v&agrave; dễ d&agrave;ng hơn cho qu&yacute; kh&aacute;ch, Tấn Phong.vn</strong>&nbsp;<strong>&nbsp;sẽ hiển thị gi&aacute; cước cho đơn h&agrave;ng của qu&yacute; kh&aacute;ch một c&aacute;ch tự động khi qu&yacute; kh&aacute;ch cung cấp th&ocirc;ng tin tỉnh/th&agrave;nh phố trong qu&aacute; tr&igrave;nh đặt h&agrave;ng online.</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>Khuyến c&aacute;o khi nhận h&agrave;ng:</strong></h3>\r\n\r\n<p><strong>Trường Đức&nbsp;</strong>khuyến c&aacute;o qu&yacute; kh&aacute;ch h&agrave;ng phải kiểm tra t&igrave;nh trạng b&ecirc;n ngo&agrave;i của th&ugrave;ng h&agrave;ng v&agrave; sản phẩm trước khi thanh to&aacute;n hoặc k&yacute; nhận h&agrave;ng để đảm bảo rằng h&agrave;ng h&oacute;a được giao đ&uacute;ng chủng loại, số lượng, m&agrave;u sắc theo đơn đặt h&agrave;ng v&agrave; t&igrave;nh trạng b&ecirc;n ngo&agrave;i kh&ocirc;ng bị t&aacute;c động (bể vỡ/ tr&agrave;y xước). Nếu gặp trường hợp n&agrave;y, qu&yacute; kh&aacute;ch vui l&ograve;ng từ chối nhận h&agrave;ng v&agrave; b&aacute;o ngay cho bộ phận hỗ trợ kh&aacute;ch h&agrave;ng thời điểm nhận h&agrave;ng từ người giao h&agrave;ng để ch&uacute;ng t&ocirc;i c&oacute; phương &aacute;n xử l&iacute; kịp thời.</p>\r\n\r\n<p>Đối với trường hợp đơn h&agrave;ng COD (trả tiền cho người giao h&agrave;ng), ngo&agrave;i c&aacute;c bước kiểm tra sơ bộ tr&ecirc;n, c&aacute;c bước kiểm tra s&acirc;u hơn như sử dụng thử sản phẩm chỉ c&oacute; thể được tiến h&agrave;nh sau khi qu&yacute; kh&aacute;ch thanh to&aacute;n cho người vận chuyển.</p>\r\n\r\n<p>Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng kiểm tra h&agrave;ng h&oacute;a khi nhận h&agrave;ng v&agrave; kh&ocirc;ng phản hồi trong 24 giờ,&nbsp;<strong>Trường Đức</strong>&nbsp;mặc nhi&ecirc;n coi l&agrave; qu&yacute; kh&aacute;ch đ&atilde; nhận đ&uacute;ng h&agrave;ng h&oacute;a đ&atilde; đặt h&agrave;ng, theo đ&uacute;ng quy chuẩn, kh&ocirc;ng c&oacute; vấn đề g&igrave; v&agrave; ch&uacute;ng t&ocirc;i c&oacute; quyền từ chối hỗ trợ cho những khiếu nại thuộc ch&iacute;nh s&aacute;ch đổi trả h&agrave;ng h&oacute;a.</p>\r\n\r\n<h3><strong>Từ chối vận chuyển:</strong></h3>\r\n\r\n<p><strong>- Trường Đức&nbsp;</strong>từ chối vận chuyển h&agrave;ng tới c&aacute;c khu vực m&agrave; đối t&aacute;c vận chuyển của ch&uacute;ng t&ocirc;i kh&ocirc;ng tới được.</p>\r\n\r\n<p><strong>- Trường Đức&nbsp;</strong>cũng từ chối vận chuyển lại tới c&aacute;c địa chỉ m&agrave; đơn h&agrave;ng COD đ&atilde; tới địa chỉ n&agrave;y, qu&yacute; kh&aacute;ch v&igrave; bất cứ l&yacute; do xuất ph&aacute;t từ kh&aacute;ch h&agrave;ng (kh&ocirc;ng th&iacute;ch, thiếu tiền, kh&ocirc;ng c&oacute; mặt tại địa chỉ khi nh&acirc;n vi&ecirc;n gọi điện 2 lần, kh&ocirc;ng li&ecirc;n lạc được v.v.) kh&ocirc;ng thanh to&aacute;n cho nh&acirc;n vi&ecirc;n vận chuyển v&agrave; h&agrave;ng h&oacute;a chuyển trả lại cho ch&uacute;ng t&ocirc;i (&nbsp;Trong trường hợp n&agrave;y, ch&uacute;ng t&ocirc;i phải chịu 2 lần cước vận chuyển n&ecirc;n qu&yacute; kh&aacute;ch th&ocirc;ng cảm).</p>\r\n\r\n<p>Trong trường hợp qu&yacute; kh&aacute;ch vẫn muốn tiếp tục mua h&agrave;ng tại&nbsp;<strong>Trường Đức</strong>, vui l&ograve;ng chuyển khoản&nbsp;trước đơn h&agrave;ng v&agrave; ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng &aacute;p dụng chế độ thanh to&aacute;n COD.</p>', 'Chính sách vận chuyển', '2023-11-20 23:43:16', '2023-11-20 23:43:16'),
(3, 'CHÍNH SÁCH ĐỔI TRẢ HÀNG HÓA TRƯỜNG ĐỨC', '<h2><strong>Qu&yacute; kh&aacute;ch c&oacute; thể trực tiếp li&ecirc;n hệ đổi h&agrave;ng tại địa chỉ sau:</strong></h2>\r\n\r\n<h3><strong>Showroom b&aacute;n h&agrave;ng Trường Đức</strong><strong>: 88 Y Moan &Ecirc;nuoi, phường T&acirc;n lợi , Buon Me Thuot, Vietnam.</strong></h3>\r\n\r\n<p><strong>SĐT:</strong>&nbsp;<strong>0989 168 262.</strong></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể thực hiện đổi sang sản phẩm kh&aacute;c miễn ph&iacute; trong v&ograve;ng 7 ng&agrave;y kể từ ng&agrave;y nhận h&agrave;ng cho c&aacute;c sản phẩm đặt mua tại&nbsp;<strong>Trường Đức</strong>.&nbsp;</p>\r\n\r\n<p>Quy tr&igrave;nh đổi trả h&agrave;ng được thực hiện khi kiện h&agrave;ng được ho&agrave;n về tại kho h&agrave;ng của&nbsp;<strong>Trường Đức</strong>, ch&uacute;ng t&ocirc;i sẽ thực hiện c&aacute;c bước tiếp theo để kiểm tra chất lượng sản phẩm v&agrave; tiến h&agrave;nh đổi/trả h&agrave;ng theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&ograve;n nguy&ecirc;n đ&oacute;ng g&oacute;i, nguy&ecirc;n seal của đ&oacute;ng g&oacute;i v&agrave; bao b&igrave; kh&ocirc;ng bị m&oacute;p r&aacute;ch.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đầy đủ c&aacute;c bộ phận, chi tiết, phụ kiện.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tem/ phiếu bảo h&agrave;nh, tem thương hiệu, hướng dẫn kỹ thuật v&agrave; c&aacute;c qu&agrave; tặng k&egrave;m theo (nếu c&oacute;) v.v&hellip; phải c&ograve;n đầy đủ v&agrave; nguy&ecirc;n vẹn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng bị dơ bẩn, trầy xước, bể vỡ, hư hỏng, c&oacute; m&ugrave;i lạ hoặc c&oacute; dấu hiệu đ&atilde; qua giặt tẩy hoặc qua sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Danh mục hạn chế (sản phẩm kh&ocirc;ng &aacute;p dụng đổi trả)&nbsp;</strong>c&aacute;c sản phẩm thanh l&yacute;, khuyến m&atilde;i, sản phẩm qu&agrave; tặng k&egrave;m, sản phẩm được đổi từ điểm t&iacute;ch lũy.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Những sản phẩm đổi trả kh&ocirc;ng đ&aacute;p ứng c&aacute;c điều kiện n&ecirc;u tr&ecirc;n sẽ kh&ocirc;ng được xử l&yacute; v&agrave; được tự động chuyển ho&agrave;n về địa chỉ đăng k&yacute; theo th&ocirc;ng tin đơn h&agrave;ng của qu&yacute; kh&aacute;ch.</p>\r\n\r\n<p><strong>Trường Đức</strong>&nbsp;khuyến kh&iacute;ch qu&yacute; kh&aacute;ch h&agrave;ng phải kiểm tra t&igrave;nh trạng b&ecirc;n ngo&agrave;i của th&ugrave;ng h&agrave;ng v&agrave; sản phẩm trước khi thanh to&aacute;n để đảm bảo rằng h&agrave;ng h&oacute;a được giao đ&uacute;ng chủng loại, số lượng, m&agrave;u sắc theo đơn đặt h&agrave;ng v&agrave; t&igrave;nh trạng b&ecirc;n ngo&agrave;i kh&ocirc;ng bị t&aacute;c động (bể vỡ/ tr&agrave;y xước). Nếu gặp trường hợp n&agrave;y, qu&yacute; kh&aacute;ch vui l&ograve;ng từ chối nhận h&agrave;ng v&agrave;/hoặc b&aacute;o ngay cho bộ phận hỗ trợ kh&aacute;ch h&agrave;ng để ch&uacute;ng t&ocirc;i c&oacute; phương &aacute;n xử l&iacute; kịp thời. (Xin lưu &yacute; những bước kiểm tra s&acirc;u hơn như d&ugrave;ng thử sản phẩm chỉ c&oacute; thể được chấp nhận sau khi đơn h&agrave;ng được thanh to&aacute;n đầy đủ).</p>\r\n\r\n<p>Trong trường hợp kh&aacute;ch h&agrave;ng đ&atilde; thanh to&aacute;n, nhận h&agrave;ng v&agrave; sau đ&oacute; ph&aacute;t hiện h&agrave;ng h&oacute;a bị bể vỡ, sai nội dung hoặc thiếu h&agrave;ng, xin vui l&ograve;ng chụp ảnh sản phẩm tại thời điểm nhận h&agrave;ng từ người vận chuyển v&agrave; th&ocirc;ng b&aacute;o cho ch&uacute;ng t&ocirc;i để được hỗ trợ.</p>\r\n\r\n<p>Sau 24h kể từ ng&agrave;y qu&yacute; kh&aacute;ch nhận h&agrave;ng,&nbsp;<strong>Trường Đức&nbsp;</strong>c&oacute; quyền từ chối hỗ trợ cho những khiếu nại theo nội dung như tr&ecirc;n.</p>\r\n\r\n<p>Sản phẩm chỉ được xem l&agrave; lỗi kỹ thuật khi được x&aacute;c nhận từ ch&iacute;nh bộ phận kỹ thuật của ch&uacute;ng t&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi qu&yacute; kh&aacute;ch gặp trục trặc với sản phẩm đặt mua tại&nbsp;<strong>Trường Đức</strong>, vui l&ograve;ng thực hiện c&aacute;c bước sau đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bước 1: Kiểm tra c&aacute;ch thức sử dụng sản phẩm, c&aacute;c thao t&aacute;c được hướng dẫn trong &ldquo;S&aacute;ch hướng dẫn sử dụng&rdquo; đi k&egrave;m với mỗi sản phẩm (nếu c&oacute;).</p>\r\n	</li>\r\n	<li>\r\n	<p>Bước 2: Qu&yacute; kh&aacute;ch li&ecirc;n hệ với trung t&acirc;m kỹ thuật/bảo h&agrave;nh nằm trong phiếu bảo h&agrave;nh của sản phẩm (nếu c&oacute;). Đối với một v&agrave;i d&ograve;ng sản phẩm kh&ocirc;ng c&oacute; giấy/s&aacute;ch hướng dẫn sử dụng, ch&uacute;ng t&ocirc;i thường đăng chi tiết hướng dẫn sử dụng tr&ecirc;n website của sản phẩm đ&oacute;. Qu&yacute; kh&aacute;ch vui l&ograve;ng truy cập v&agrave; đọc kỹ hướng dẫn sử dụng n&agrave;y.</p>\r\n	</li>\r\n	<li>Bước 3: Liện hệ tới bộ phận kỹ thuật của ch&uacute;ng t&ocirc;i tr&ecirc;n giấy bảo h&agrave;nh.</li>\r\n	<li>\r\n	<p>Bước 3: Trong v&ograve;ng 07 ng&agrave;y kể từ ng&agrave;y nhận h&agrave;ng, nếu qu&yacute; kh&aacute;ch được x&aacute;c nhận từ trung t&acirc;m kỹ thuật hoặc trung t&acirc;m bảo h&agrave;nh của sản phẩm hoặc từ ch&iacute;nh ch&uacute;ng t&ocirc;i rằng sản phẩm bị lỗi kỹ thuật, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ với ch&uacute;ng t&ocirc;i về việc đổi trả h&agrave;ng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Theo kinh nghiệm của ch&uacute;ng t&ocirc;i, hơn một nửa sản phẩm ho&agrave;n về v&igrave; l&iacute; do lỗi kỹ thuật sau khi được bộ phận kỹ thuật kiểm tra đều được đ&aacute;nh gi&aacute; sản phẩm nằm trong điều kiện sử dụng ho&agrave;n hảo. V&igrave; vậy để tiết kiệm thời gian v&agrave; thuận tiện cho qu&yacute; kh&aacute;ch, vui l&ograve;ng đọc kĩ hướng dẫn sử dụng (đi k&egrave;m sản phẩm hoặc tr&ecirc;n website) hoặc li&ecirc;n hệ trực tiếp với bộ phận hỗ trợ kỹ thuật của sản phẩm hoặc&nbsp;<strong>Hotline</strong>&nbsp;của ch&uacute;ng t&ocirc;i để chắc rằng sản phẩm đ&atilde; được lắp r&aacute;p, kết nối v&agrave; vận h&agrave;nh ch&iacute;nh x&aacute;c trước khi gửi h&agrave;ng về lại&nbsp;<strong>Trường Đức.</strong>&nbsp;.<strong>&nbsp;</strong></p>\r\n\r\n<p>Trong trường hợp sản phẩm ho&agrave;n về kh&ocirc;ng c&oacute; lỗi kỹ thuật từ nh&agrave; sản xuất, m&agrave; do qu&yacute; kh&aacute;ch chưa biết c&aacute;ch sử dụng, ch&uacute;ng t&ocirc;i sẽ chuyển trả lại đ&uacute;ng sản phẩm ban đầu cho qu&yacute; kh&aacute;ch v&agrave; qu&yacute; kh&aacute;ch sẽ chịu chi ph&iacute; vận chuyển n&agrave;y.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Đối với to&agrave;n bộ c&aacute;c loại ổ cắm, c&ocirc;ng tắc th&ocirc;ng minh, ch&uacute;ng t&ocirc;i kh&ocirc;ng nhận đổi trả hoặc bảo h&agrave;nh nếu qu&yacute; kh&aacute;ch trong qu&aacute; tr&igrave;nh sử dụng, l&agrave;m ch&aacute;y nổ, ch&aacute;y rơle do sử dụng thiết bị ti&ecirc;u thụ điện sai c&ocirc;ng suất, điện &aacute;p, cường độ d&ograve;ng khởi động vượt qu&aacute; thiết kế của ổ cắm, c&ocirc;ng tắc, hoặc bị s&eacute;t đ&aacute;nh v.v&hellip; dẫn tới hư hại sản phẩm. Đ&acirc;y kh&ocirc;ng phải lỗi kỹ thuật của thiết bị m&agrave; l&agrave; lỗi đến từ việc kh&ocirc;ng đọc kỹ hướng dẫn sử dụng hoặc do thi&ecirc;n tai bất khả kh&aacute;ng qu&yacute; kh&aacute;ch th&ocirc;ng cảm.</p>', 'Chính sách đổi trả', '2023-11-20 23:54:24', '2023-11-20 23:54:24'),
(4, 'CHÍNH SÁCH BẢO MẬT TRƯỜNG ĐỨC', '<p>Ch&uacute;ng t&ocirc;i sẽ thu thập nhiều th&ocirc;ng tin kh&aacute;c nhau của qu&yacute; kh&aacute;ch khi bạn muốn đặt h&agrave;ng tr&ecirc;n web.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i thu thập, lưu trữ v&agrave; xử l&yacute; th&ocirc;ng tin của bạn cho qu&aacute; tr&igrave;nh mua h&agrave;ng v&agrave; cho những th&ocirc;ng b&aacute;o sau n&agrave;y, v&agrave; để cung cấp dịch vụ. Ch&uacute;ng t&ocirc;i kh&ocirc;ng giới hạn th&ocirc;ng tin c&aacute; nh&acirc;n: danh hiệu, t&ecirc;n, giới t&iacute;nh, ng&agrave;y sinh, email, địa chỉ, địa chỉ giao h&agrave;ng, số điện thoại, fax, chi tiết thanh to&aacute;n, chi tiết thanh to&aacute;n bằng thẻ hoặc chi tiết t&agrave;i khoản ng&acirc;n h&agrave;ng.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i sẽ d&ugrave;ng th&ocirc;ng tin qu&yacute; kh&aacute;ch đ&atilde; cung cấp để xử l&yacute; đơn đặt h&agrave;ng, cung cấp c&aacute;c dịch vụ v&agrave; th&ocirc;ng tin y&ecirc;u cầu th&ocirc;ng qua website v&agrave; theo y&ecirc;u cầu của bạn. Hơn nữa, ch&uacute;ng t&ocirc;i sẽ sử dụng c&aacute;c th&ocirc;ng tin đ&oacute; để quản l&yacute; t&agrave;i khoản của bạn; x&aacute;c minh v&agrave; thực hiện giao dịch trực tuyến, kiểm to&aacute;n việc tải dữ liệu từ web; cải thiện bố cục v&agrave; nội dung trang web v&agrave; điều chỉnh cho ph&ugrave; hợp với người d&ugrave;ng; nhận diện kh&aacute;ch v&agrave;o web, nghi&ecirc;n cứu nh&acirc;n khẩu học, gửi th&ocirc;ng tin bao gồm th&ocirc;ng tin sản phẩm v&agrave; dịch vụ, nếu bạn kh&ocirc;ng c&oacute; dấu hiệu từ chối. Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng muốn nhận bất cứ th&ocirc;ng tin tiếp thị của ch&uacute;ng t&ocirc;i th&igrave; c&oacute; thể từ chối bất cứ l&uacute;c n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i c&oacute; thể chuyển t&ecirc;n v&agrave; địa chỉ cho b&ecirc;n thứ ba để họ giao h&agrave;ng cho bạn (v&iacute; dụ cho b&ecirc;n chuyển ph&aacute;t nhanh hoặc nh&agrave; cung cấp).</p>\r\n\r\n<p>C&aacute;c khoản thanh to&aacute;n trực tuyến sẽ được xử l&yacute; bởi c&aacute;c đại l&yacute; của ch&uacute;ng t&ocirc;i. Qu&yacute; kh&aacute;ch chỉ đưa cho ch&uacute;ng t&ocirc;i hoặc Đại l&yacute; hoặc website những th&ocirc;ng tin ch&iacute;nh x&aacute;c, kh&ocirc;ng g&acirc;y nhầm lẫn v&agrave; phải th&ocirc;ng b&aacute;o cho ch&uacute;ng t&ocirc;i nếu c&oacute; thay đổi.</p>\r\n\r\n<p>Chi tiết đơn đặt h&agrave;ng của bạn được ch&uacute;ng t&ocirc;i lưu giữ nhưng v&igrave; l&iacute; do bảo mật n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng c&ocirc;ng khai trực tiếp được. Tuy nhi&ecirc;n, qu&yacute; kh&aacute;ch c&oacute; thể tiếp cận th&ocirc;ng tin bằng c&aacute;ch đăng nhập t&agrave;i khoản tr&ecirc;n web. Tại đ&acirc;y, bạn sẽ thấy chi tiết đơn đặt h&agrave;ng của m&igrave;nh, những sản phẩm đ&atilde; nhận v&agrave; những sản phẩm đ&atilde; gửi v&agrave; chi tiết email, ng&acirc;n h&agrave;ng v&agrave; bản tin m&agrave; bạn đặt theo d&otilde;i d&agrave;i hạn. Qu&yacute; kh&aacute;ch cam kết bảo mật dữ liệu c&aacute; nh&acirc;n v&agrave; kh&ocirc;ng được ph&eacute;p tiết lộ cho b&ecirc;n thứ ba. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu bất kỳ tr&aacute;ch nhiệm n&agrave;o cho việc d&ugrave;ng sai mật khẩu nếu đ&acirc;y kh&ocirc;ng phải lỗi của ch&uacute;ng t&ocirc;i.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; quyền y&ecirc;u cầu truy cập v&agrave;o dữ liệu c&aacute; nh&acirc;n của m&igrave;nh, c&oacute; quyền y&ecirc;u cầu ch&uacute;ng t&ocirc;i sửa lại những sai s&oacute;t trong dữ liệu của bạn m&agrave; kh&ocirc;ng mất ph&iacute;. Bất cứ l&uacute;c n&agrave;o bạn cũng c&oacute; quyền y&ecirc;u cầu ch&uacute;ng t&ocirc;i ngưng sử dụng dữ liệu c&aacute; nh&acirc;n của bạn cho mục đ&iacute;ch tiếp thị.</p>', 'Chính sách bảo mật', '2023-11-21 00:03:01', '2023-11-21 00:03:01'),
(5, 'CHÍNH SÁCH THANH TOÁN TRƯỜNG ĐỨC', '<h3><strong>Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn c&aacute;c phương thức thanh to&aacute;n sau:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Trả tiền mặt trực tiếp cho người giao h&agrave;ng tại địa chỉ kh&aacute;ch h&agrave;ng(COD &ndash; Cash On Delivery):</strong>&nbsp;Đ&acirc;y l&agrave; h&igrave;nh thức ch&uacute;ng t&ocirc;i sẽ th&ocirc;ng qua c&aacute;c c&ocirc;ng ty vận chuyển chuyển ph&aacute;t h&agrave;ng h&oacute;a tới địa chỉ của kh&aacute;ch h&agrave;ng v&agrave; người giao h&agrave;ng sẽ thu hộ tiền mặt từ qu&yacute; kh&aacute;ch l&uacute;c giao h&agrave;ng.</p>\r\n\r\n	<p><strong>Lưu &yacute;:</strong>&nbsp;H&igrave;nh thức n&agrave;y chỉ nhận ph&aacute;t h&agrave;ng thu tiền trong giờ h&agrave;nh ch&iacute;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chuyển tiền v&agrave;o t&agrave;i khoản ng&acirc;n h&agrave;ng của&nbsp;</strong><strong>Trường Đức</strong><strong>:</strong>&nbsp;Đ&acirc;y l&agrave; h&igrave;nh thức qu&yacute; kh&aacute;ch th&ocirc;ng qua m&aacute;y ATM, hoặc ra c&aacute;c ph&ograve;ng giao dịch của c&aacute;c ng&acirc;n h&agrave;ng để chuyển tiền v&agrave;o t&agrave;i khoản đ&atilde; c&ocirc;ng bố của&nbsp;<strong>Trường Đức</strong><strong>&nbsp;</strong>(C&aacute;c t&agrave;i khoản ng&acirc;n h&agrave;ng cũng sẽ hiện ra ở bước cuối trong qu&aacute; tr&igrave;nh đặt h&agrave;ng online v&agrave; gửi về email của qu&yacute; kh&aacute;ch khi bấm n&uacute;t Mua h&agrave;ng).</p>\r\n\r\n	<p>Với c&aacute;c t&agrave;i khoản kh&aacute;c hệ thống ng&acirc;n h&agrave;ng hoặc kh&aacute;c v&ugrave;ng miền địa l&yacute;, th&ocirc;ng thường tiền sẽ được ng&acirc;n h&agrave;ng chuyển chậm hơn trong v&ograve;ng 24h.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chuyển khoản Online:</strong>&nbsp;Qu&yacute; kh&aacute;ch đăng k&yacute; dịch vụ Internet Banking tại c&aacute;c ng&acirc;n h&agrave;ng v&agrave; lập lệnh chuyển tiền online tr&ecirc;n hệ thống.Với c&aacute;c t&agrave;i khoản kh&aacute;c hệ thống ng&acirc;n h&agrave;ng hoặc kh&aacute;c v&ugrave;ng miền địa l&yacute;, th&ocirc;ng thường tiền sẽ được ng&acirc;n h&agrave;ng chuyển chậm hơn trong v&ograve;ng 24h.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Trả tiền trực tiếp tại showroom:</strong>&nbsp;Qu&yacute; kh&aacute;ch đến trực tiếp mua h&agrave;ng v&agrave; trả tiền cho ch&uacute;ng t&ocirc;i.&nbsp;<strong>Địa chỉ showroom:</strong>&nbsp;Số 88, Y Moan &Ecirc;nuoi, Phường T&acirc;n Lợi, Bu&ocirc;n Ma Thuật, ĐăkLăk.</p>\r\n	</li>\r\n</ul>', 'Phương thức thanh toán', '2023-11-21 00:06:32', '2023-11-21 00:06:32'),
(6, 'THÔNG TIN CHUYỂN KHOẢN TRƯỜNG ĐỨC', '<p><img src=\"https://file.hstatic.net/1000162838/file/ng_n_h_ng_acb_grande.png\" style=\"height:175px; width:319px\" /></p>\r\n\r\n<p><strong>ACB:&nbsp;3969797, Trịnh Ho&agrave;ng Nam, PGD Ho&agrave;ng Cầu.</strong></p>', 'Thông tin chuyển khoản', '2023-11-21 00:09:57', '2023-11-21 00:09:57');
